import React, { useState, useEffect } from "react";
import { useParams, Link } from "react-router-dom";
import axios from "axios";
import Table from "react-bootstrap/Table";
import Button from "react-bootstrap/Button";
import Modal from "react-bootstrap/Modal";
import Form from "react-bootstrap/Form";
import Swal from "sweetalert2";
import withReactContent from "sweetalert2-react-content";

function SubCategoryDetail() {
  const [subcategory, setSubcategory] = useState(null);
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showModal, setShowModal] = useState(false);
  const [productName, setProductName] = useState("");

  const { categoryId, subcategoryId } = useParams();
  const MySwal = withReactContent(Swal);

  useEffect(() => {
    // Fetch subcategory details
    axios
      .get(`/api/categories/subcategory/subcategories/${subcategoryId}`)
      .then((response) => {
        setSubcategory(response.data.subcategory);
        setLoading(false);
      })
      .catch((error) => {
        console.error("Error fetching subcategory:", error);
        setLoading(false);
      });

    // Fetch products for the current subcategory
    axios
      .get(`/api/products/get-products-by-subcategory/${subcategoryId}`)
      .then((response) => {
        setProducts(response.data);
      })
      .catch((error) => {
        console.error("Error fetching products:", error);
      });
  }, [subcategoryId]);

  const handleShowModal = () => {
    setShowModal(true);
  };

  const handleCloseModal = () => {
    setShowModal(false);
  };

  const handleCreateProduct = async () => {
    try {
      const response = await fetch("/api/products/create-product", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ name: productName, subcategoryId }),
      });

      if (response.status === 201) {
        MySwal.fire({
          title: <strong>Product created successfully</strong>,
          html: <i></i>,
          icon: 'success',
        });

        // Close the modal and reset the product name
        handleCloseModal();
        setProductName("");

        // Fetch the updated list of products for the subcategory
        const productResponse = await axios.get(`/api/products/get-products-by-subcategory/${subcategoryId}`);
        setProducts(productResponse.data);
      } else {
        alert("Product creation failed");
      }
    } catch (error) {
      console.error("Error creating product", error);
      alert("Product creation failed");
    }
  };

  if (loading) {
    return <div>Loading...</div>;
  }

  if (!subcategory) {
    return <div>SubCategory not found</div>;
  }

  return (
    <div className="container">
      <div className="buttons_top">
        <Link to={`/category/${categoryId}`} className="btn btn-secondary">
          Back
        </Link>
        &nbsp;
        <Button onClick={handleShowModal} className="btn btn-primary">
          Add Products
        </Button>
      </div>
      <br/>
      <h1>{subcategory.name} </h1>

      <Table striped>
        <thead>
          <tr>
            <th>Products({subcategory.products.length})</th>
          </tr>
        </thead>
        <tbody>
          {products.map((product) => (
            <tr key={product._id}>
              <td>{product.name}</td>
            </tr>
          ))}
        </tbody>
      </Table>

      <Modal show={showModal} onHide={handleCloseModal}>
        <Modal.Header closeButton>
          <Modal.Title>Create Product</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <Form.Group controlId="productName">
              <Form.Label>Product Name</Form.Label>
              <Form.Control
                type="text"
                value={productName}
                onChange={(e) => setProductName(e.target.value)}
              />
            </Form.Group>
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleCloseModal}>
            Cancel
          </Button>
          <Button variant="primary" onClick={handleCreateProduct}>
            Create Product
          </Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
}

export default SubCategoryDetail;
