import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import '../App.css'
import { Link } from 'react-router-dom'
import Table from "react-bootstrap/Table";
import { Modal, Button, Form } from "react-bootstrap";


function CategoryDetail() {
    const [category, setCategory] = useState(null);
    const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);

  const { categoryId } = useParams();

    const handleShowModal = () => {
      setShowModal(true);
    };

    const handleCloseModal = () => {
      setShowModal(false);
    };
    const MySwal = withReactContent(Swal)

    const handleSubmit = async (e) => {
      e.preventDefault();

      try {
        const response = await fetch("/api/categories/create-category", {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify({ name: categoryName }),
        });

        if (response.status === 201) {
          MySwal.fire({
            title: <strong>Category created successfully</strong>,
            html: <i></i>,
            icon: 'success',
        });

          setCategoryName("");
          handleCloseModal();
          navigate("/");
          axios
        .get("/api/categories/viewCategories")
        .then((response) => {
          setCategories(response.data.categories);
        })
        .catch((error) => {
          console.error("Error fetching categories:", error);
        });

        } else {
          alert("Category creation failed");
        }
      } catch (error) {
        console.error("Error creating category", error);
        alert("Category creation failed");
      }
    };

  useEffect(() => {
    axios
      .get(`/api/categories/${categoryId}`)
      .then((response) => {
        // console.log(categoryId);
          setCategory(response.data);
          // console.log(response.data);
        setLoading(false);
      }) 
      .catch((error) => {
        console.error("Error fetching category:", error);
        setLoading(false);
      });
    //   products
      axios
        .get(`/api/products/get-products-by-category/${categoryId}`)
        .then((response) => {
          setProducts(response.data);
        })
        .catch((error) => {
          console.error("Error fetching products:", error);
        });

  }, [categoryId]);

  if (loading) {
    return <div>Loading...</div>;
  }

  if (!category) {
    return <div>Category not found</div>;
  }

  return (
    <div className="container">
      <div className="buttons_top w-100">
        <Link to={`/`} className="btn btn-success">
          home
        </Link>
        &nbsp;
        <Button 
          onClick={handleShowModal} 
          className="btn btn-primary justify-content-end"
        >
          Add SubCategory
        </Button>
      </div>
      <br/>
      <h1 className="">{category.name}</h1>
      <br/>
      <h4>Subcategories({category.subcategories.length})</h4>
      <ul className="subcat_list">
        {category.subcategories.map((subcategory) => (
          <Button to={`/category/${categoryId}/${subcategory._id}`}>
            <li key={subcategory._id}>{subcategory.name}</li>
          </Button>
        ))}
      </ul>


      <Table striped>
        <thead>
          <tr>
            <th>Products</th>
          </tr>
        </thead>
        <tbody>
          {products.map((product) => (
            <tr key={product._id}>
              <td>{product.name}</td>
            </tr>
          ))}
        </tbody>
      </Table>
    </div>
  );
}

export default CategoryDetail;
