import React, { useState, useEffect } from "react";
import { useParams, Link } from "react-router-dom";
import axios from "axios";
import Table from "react-bootstrap/Table";
import { Modal, Button, Form } from "react-bootstrap";
import Swal from "sweetalert2";
import withReactContent from "sweetalert2-react-content";
import { useNavigate } from "react-router-dom";

function CategoryDetail() {
  const [category, setCategory] = useState(null);
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [categoryName, setCategoryName] = useState("");
  const [showModal, setShowModal] = useState(false);
  const [categories, setCategories] = useState([]);

  const { categoryId } = useParams();

  const handleShowModal = () => {
    setShowModal(true);
  };
  const navigate = useNavigate();

  const handleCloseModal = () => {
    setShowModal(false);
  };
  const MySwal = withReactContent(Swal);

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      // Make an API request to create the subcategory
      const response = await axios.post(
        `/api/categories/${categoryId}/create-subcategory`,
        { name: categoryName }
      );

      if (response.status === 201) {
        // Subcategory created successfully
        MySwal.fire({
          title: <strong>SubCategory created successfully</strong>,
          html: <i></i>,
          icon: "success",
        });

        // Clear the input field and close the modal
        setCategoryName("");
        handleCloseModal();

        // Update the category state with the new subcategory
        setCategory((prevCategory) => ({
          ...prevCategory,
          subcategories: [...prevCategory.subcategories, response.data],
        }));
      } else {
        // Handle error if subcategory creation fails
        alert("SubCategory creation failed");
      }
    } catch (error) {
      // Handle any network or other errors
      console.error("Error creating subcategory", error);
      alert("SubCategory creation failed");
    }
  };




  useEffect(() => {
    // Fetch the category details
    axios
      .get(`/api/categories/${categoryId}`)
      .then((response) => {
        setCategory(response.data);
        setLoading(false);
      })
      .catch((error) => {
        console.error("Error fetching category:", error);
        setLoading(false);
      });

    // Fetch the products for the current category
    axios
      .get(`/api/products/get-products-by-category/${categoryId}`)
      .then((response) => {
        setProducts(response.data);
      })
      .catch((error) => {
        console.error("Error fetching products:", error);
      });

    // Fetch the updated list of categories
    axios
      .get("/api/categories/viewCategories")
      .then((response) => {
        setCategories(response.data.categories);
      })
      .catch((error) => {
        console.error("Error fetching categories:", error);
      });
  }, [categoryId]);

  if (loading) {
    return <div>Loading...</div>;
  }

  if (!category) {
    return <div>Category not found</div>;
  }

  return (
    <div className="container">
      <div className="buttons_top w-100">
        <Link to={`/`} className="btn btn-success">
          Home
        </Link>
        &nbsp;
        <Button
          onClick={handleShowModal}
          className="btn btn-primary justify-content-end"
        >
          Add SubCategory
        </Button>
      </div>
      <br />
      <h1 className="">{category.name}</h1>
      <br />
      <h4>Subcategories({category.subcategories.length})</h4>
      <ul className="subcat_list">
        {category.subcategories.map((subcategory) => (
          <Button to={`/category/${categoryId}/${subcategory._id}`} key={subcategory._id}>
            <li>{subcategory.name}</li>
          </Button>
        ))}
      </ul>

      <Table striped>
        <thead>
          <tr>
            <th>Products</th>
          </tr>
        </thead>
        <tbody>
          {products.map((product) => (
            <tr key={product._id}>
              <td>{product.name}</td>
            </tr>
          ))}
        </tbody>
      </Table>

      {/* Add category modal */}
      <Modal show={showModal} onHide={handleCloseModal}>
        <Modal.Header closeButton>
          <Modal.Title>Create a SubCategory</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form onSubmit={handleSubmit}>
            <Form.Group controlId="categoryName">
              <Form.Label>SubCategory Name:</Form.Label>
              <Form.Control
                type="text"
                value={categoryName}
                onChange={(e) => setCategoryName(e.target.value)}
              />
            </Form.Group>
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleCloseModal}>
            Cancel
          </Button>
          <Button variant="primary" onClick={handleSubmit}>
            Create SubCategory
          </Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
}

export default CategoryDetail;
